package main

import (
	// "encoding/hex"
	"fmt"
	"net"
	// "strconv"
	"time"
)

func CheckError(err error) {
	if err != nil {
		fmt.Println("Error: ", err)
	}
}

func main() {
	ServerAddr, err := net.ResolveUDPAddr("udp", "192.168.100.5:10001")
	CheckError(err)

	LocalAddr, err := net.ResolveUDPAddr("udp", "192.168.100.4:0")
	CheckError(err)

	Conn, err := net.DialUDP("udp", LocalAddr, ServerAddr)
	CheckError(err)

	defer Conn.Close()
	str := ""
	// packetin, _ := hex.DecodeString(str)
	i := 0
	t0 := time.Now()
	for {
		buf := []byte(str)
		t1 := time.Now()
		fmt.Printf("Program start time:%s,sent:%06d,sent time:%40s,Elapsed time:%13s,Avg:%.3f\n", t0, i, t1, t1.Sub(t0), float64(i)/t1.Sub(t0).Seconds())
		// fmt.Println("Program Start Time:", t0, "sent:", i, "sent time:", t1, "Elapsed time:", t1.Sub(t0), "avg:", float64(i)/t1.Sub(t0).Seconds())
		// msg := strconv.Itoa(str)
		_, err := Conn.Write(buf)
		i++
		if err != nil {
			fmt.Println(str, err)
		}
		time.Sleep(time.Microsecond * 30) // 0.03ms
		// time.Sleep(time.Second * 5)
	}
}
